package pan.springbootkit.aspect.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import pan.springbootkit.aspect.service.PanAspectService;import pan.springbootkit.base.BaseResult;/** * 切面 controller * * Created by panzhangbao on 1/12/18. * Copyright © 2017年 panzhangbao. All rights reserved. */@RestController@RequestMapping("api/aop")public class PanAspectController {    @Autowired	PanAspectService panAspectService;    /**     * 切面编程     */    @GetMapping    public BaseResult get(@RequestParam(value = "foodName", defaultValue = "小苹果") String foodName,						  @RequestParam(value = "movieName", defaultValue = "《金刚》") String movieName) {        panAspectService.eatFood(foodName);        panAspectService.watchMovie(movieName);        return BaseResult.success();    }}