package pan.springbootkit.file;import org.springframework.web.bind.annotation.*;import org.springframework.web.multipart.MultipartFile;/** * 文件 controller * * Created by panzhangbao on 11/3/17. * Copyright © 2017年 panzhangbao. All rights reserved. */@RestController@RequestMapping("api/file")public class FileController {	/**	 * 文件下载	 *	 * @return java.lang.String	 * @date 2019-09-12 16:29	 * @author panzhangbao	 */	@GetMapping	public void download(){		PanFileUtil.localServerDownload();	}    /**     * 文件上传     * @param file     * @return     */    @PostMapping    public String update(@RequestParam("file") MultipartFile file){        return PanFileUtil.localServerUploadFile(file);    }}